package SeleniumClass;
/*10. Print the following series: 
1.0 2.0 3.0  ...... 10.0 
0 5 10 15 20 25 …. 100
*/
public class Assign10WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int i=0;
	while(i<=100) {
	 System.out.println(i);
	 i=i+5;
	}
	
	System.out.println("---------------------------------------------------");
	 float j=1;
	 while(j<=10) {
		 System.out.println(j);
		 j++;
	 }
				
	}

}
